import { HttpErrorResponse } from "@angular/common/http";
import { Component } from "@angular/core";
import { Router } from "@angular/router";

import { AuthenticationService } from "src/app/service/authentication.service";
import { NotifService } from "src/app/service/notif.service";
import { NotificationService } from "src/app/service/notification.service";

@Component({
  selector: "app-forget-password",
  templateUrl: "./forget-password.component.html",
  styleUrls: ["./forget-password.component.css"],
})
export class ForgetPasswordComponent {
  public email: string;
  public isLoadding = false;
  public overlayText: string = "";

  // constructor, ngOn
  constructor(
    private router: Router,
    private authService: AuthenticationService,
    private notifService: NotificationService
  ) {}

  // public functions
  // 1
  public sendEmail(): void {
    this.turnOnOverlay("Vui lòng đợi...");
    this.authService.checkEmailForForgetPassword(this.email).subscribe({
      next: () => {
        this.notifService.success("Kiểm tra email của bạn để lấy mã xác nhận!");
        this.router.navigate([`/change-password/${this.email}`]);
        this.turnOffOverlay("");
      },
      error: (errRes: HttpErrorResponse) => {
        this.notifService.error(errRes.error.message);
        this.turnOffOverlay("");
      },
    });
  }

  // private functions
  // 1
  private turnOnOverlay(text: string): void {
    this.overlayText = text;
    this.isLoadding = true;
  }

  // 2
  private turnOffOverlay(text: string): void {
    this.overlayText = text;
    this.isLoadding = false;
  }
}
<section style="margin-bottom: 120px">
  <div class="px-4 py-5 px-md-5 text-center text-lg-start">
    <div class="container">
      <div class="row gx-lg-5 align-items-center">
        <div class="col-lg-6 mb-5 mb-lg-0">
          <h1 class="my-5 display-3 fw-bold ls-tight">
            Bee Shirt <br />
            <span style="color: #ffc107">Đẹp trong từng thước vải</span>
          </h1>
          <p style="color: hsl(217, 10%, 50.8%)">
            Bee Shirt đang tạo ra những bộ trang phục sản xuất trong nước hoàn
            toàn có thể sánh ngang với các thương hiệu thời trang nam đến từ
            nước ngoài về kiểu dáng, chất lượng lẫn phong cách thời trang.” -
            CEO Bee Shirt
          </p>
        </div>

        <div class="col-lg-6 mb-5 mb-lg-0">
          <div class="form-container">
            <h3 class="title">Quên mật khẩu</h3>
            <form class="form-horizontal">
              <div class="mb-1">
                <label>Email</label>
                <input
                  class="form-control"
                  placeholder="Email"
                  type="text"
                  name="email"
                  [(ngModel)]="email"
                />
              </div>
              <span class="signin-link mb-3"
                >Vui lòng nhập email bạn đã đăng ký để đổi mật khẩu.</span
              >
              <span class="signin-link"
                >Bạn đã có tài khoản?
                <a routerLink="/log-in">Đăng nhập</a></span
              >
              <span class="signin-link"
                >Bạn chưa có tài khoản?
                <a routerLink="/sign-up">Đăng ký</a></span
              >
              <button class="btn signup" type="submit" (click)="sendEmail()">
                Gửi
                <!-- <i *ngIf="isLoading" class="fa-solid fa-spin fa-spinner"></i> -->
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
<app-overlay *ngIf="isLoadding" [text]="overlayText"></app-overlay>
