export class SanPhamComponent {
  // constructor, ngOn
  constructor(currencyPipe, productService, colorService, sizeService, formService, designService, sleeveService, collarService, materialService, notifService, saleEventService) {
    this.currencyPipe = currencyPipe;
    this.productService = productService;
    this.colorService = colorService;
    this.sizeService = sizeService;
    this.formService = formService;
    this.designService = designService;
    this.sleeveService = sleeveService;
    this.collarService = collarService;
    this.materialService = materialService;
    this.notifService = notifService;
    this.saleEventService = saleEventService;
    this.activeColors = [];
    this.activeSizes = [];
    this.activeForms = [];
    this.activeDesigns = [];
    this.activeSleeves = [];
    this.activeCollars = [];
    this.activeMaterials = [];
    this.initColorIds = [];
    this.initSizeIds = [];
    this.initFormIds = [];
    this.initDesignIds = [];
    this.initCollarIds = [];
    this.initSleeveIds = [];
    this.initMaterialIds = [];
    this.minPrice = 0;
    this.maxPrice = 999999999;
    this.selectedColorIds = [];
    this.selectedSizeIds = [];
    this.selectedFormIds = [];
    this.selectedDesignIds = [];
    this.selectedCollarIds = [];
    this.selectedSleeveIds = [];
    this.selectedMaterialIds = [];
    this.isLoadding = false;
    this.overlayText = "";
  }
  ngOnInit() {
    this.turnOnOverlay("Vui lòng chờ");
    this.getPropertiesForFilter();
    setTimeout(() => {
      this.getInitProductList();
      this.turnOffOverlay("");
    }, 1000);
  }
  // public functions
  //
  formatPrice(price) {
    return this.currencyPipe.transform(price, "VND", "symbol", "1.0-0");
  }
  // 1
  displayPrice(prod) {
    const priceArr = [];
    for (let spct of prod.sanPhamChiTiets) {
      priceArr.push(spct.giaBan);
    }
    const minPrice = Math.min(...priceArr);
    const maxPrice = Math.max(...priceArr);
    if (minPrice === maxPrice) {
      return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0");
    }
    return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0") + " - " + this.currencyPipe.transform(maxPrice, "VND", "symbol", "1.0-0");
  }
  displayDiscountPrice(prod) {
    const priceArr = [];
    for (let spct of prod.sanPhamChiTiets) {
      let saleEvent = prod.saleEvent;
      priceArr.push(Math.round(spct.giaBan * (100 - saleEvent.giaTriPhanTram) / 100));
    }
    const minPrice = Math.min(...priceArr);
    const maxPrice = Math.max(...priceArr);
    if (minPrice === maxPrice) {
      return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0");
    }
    return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0") + " - " + this.currencyPipe.transform(maxPrice, "VND", "symbol", "1.0-0");
  }
  // 2
  getMauSacList(sanPham) {
    const mauSacs = [];
    for (let spct of sanPham.sanPhamChiTiets) {
      if (!this.checkColorExist(mauSacs, spct.mauSac.id)) {
        mauSacs.push(spct.mauSac);
      }
    }
    return mauSacs;
  }
  // 3
  toggleProperty(event, type, propertyId) {
    let isAllEmpty;
    const isChecked = event.target.checked;
    // color
    if (type === "color") {
      if (!this.selectedColorIds.some(id => id === propertyId) && isChecked) {
        this.selectedColorIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedColorIds = this.selectedColorIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // size
    if (type === "size") {
      if (!this.selectedSizeIds.some(id => id === propertyId) && isChecked) {
        this.selectedSizeIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedSizeIds = this.selectedSizeIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // form
    if (type === "form") {
      if (!this.selectedFormIds.some(id => id === propertyId) && isChecked) {
        this.selectedFormIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedFormIds = this.selectedFormIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // design
    if (type === "design") {
      if (!this.selectedDesignIds.some(id => id === propertyId) && isChecked) {
        this.selectedDesignIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedDesignIds = this.selectedDesignIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // collar
    if (type === "collar") {
      if (!this.selectedCollarIds.some(id => id === propertyId) && isChecked) {
        this.selectedCollarIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedCollarIds = this.selectedCollarIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // sleeve
    if (type === "sleeve") {
      if (!this.selectedSleeveIds.some(id => id === propertyId) && isChecked) {
        this.selectedSleeveIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedSleeveIds = this.selectedSleeveIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    // material
    if (type === "material") {
      if (!this.selectedMaterialIds.some(id => id === propertyId) && isChecked) {
        this.selectedMaterialIds.push(propertyId);
      } else if (!isChecked) {
        this.selectedMaterialIds = this.selectedMaterialIds.filter(id => id !== propertyId);
        isAllEmpty = this.checkAllSelectedIdList();
      }
    }
    if (isAllEmpty) {
      this.getInitProductList();
    } else {
      this.productService.getByFilterForClient(1, 8, this.selectedColorIds, this.selectedSizeIds, this.selectedFormIds, this.selectedDesignIds, this.selectedCollarIds, this.selectedSleeveIds, this.selectedMaterialIds, 0, 0).subscribe({
        next: response => {
          this.pagedResponse = response;
          this.getSaleEventForProduct(this.pagedResponse);
        },
        error: errResp => {
          this.notifService.error(errResp.error.message);
        }
      });
    }
  }
  checkAllSelectedIdList() {
    if (this.selectedColorIds.length === 0 && this.selectedSizeIds.length === 0 && this.selectedFormIds.length === 0 && this.selectedDesignIds.length === 0 && this.selectedCollarIds.length === 0 && this.selectedSleeveIds.length === 0 && this.selectedMaterialIds.length === 0) {
      return true;
    }
    return false;
