export class MyAddressesComponent {
  // constructor, ngOn
  constructor(addressService, authService, giaoHangNhanhService, notifService) {
    this.addressService = addressService;
    this.authService = authService;
    this.giaoHangNhanhService = giaoHangNhanhService;
    this.notifService = notifService;
    this.addresses = [];
    this.isAddAddressModalShow = false;
    this.isUpdateAddressModalShow = false;
  }
  ngOnInit() {
    this.getAllAddresses();
    this.getAllProvinces();
    this.initAddAddressForm();
    this.initUpdateAddressForm();
  }
  // public functions
  // 1
  toggleAddAddressesModal(value) {
    this.isAddAddressModalShow = value;
    if (!value) {
      this.initAddAddressForm();
      this.districts = [];
      this.wards = [];
    }
  }
  // 2
  toggleUpdateAddressesModal(value) {
    this.isUpdateAddressModalShow = value;
    if (value) {
      this.districts = [];
      this.wards = [];
    }
  }
  // 3
  formatAddress(address) {
    return `${address.duong}, ${address.xa}, ${address.huyen}, ${address.tinh}`;
  }
  // 4
  addAddress() {
    Swal.fire({
      title: "Thêm địa chỉ?",
      cancelButtonText: "Hủy",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Thêm"
    }).then(result => {
      if (result.isConfirmed) {
        this.addAddressForm.get("custId").setValue(this.authService.getCustomerFromStorage().id);
        if (this.addAddressForm.invalid) {
          this.notifService.warning("Vui lòng điền đầy đủ thông tin!");
          return;
        }
        let trimmedHoTen = this.addAddressForm.get("hoTen").value.trim();
        this.addAddressForm.get("hoTen")?.setValue(trimmedHoTen);
        let trimmedDiaChi = this.addAddressForm.get("duong").value.trim();
        this.addAddressForm.get("duong")?.setValue(trimmedDiaChi);
        this.addressService.addAddress(this.addAddressForm.value).subscribe({
          next: () => {
            this.getAllAddresses();
            this.notifService.success("Thêm địa chỉ thành công!");
            this.isAddAddressModalShow = false;
            this.initAddAddressForm();
          },
          error: errorRes => {
            this.notifService.error(errorRes.error.message);
          }
        });
      }
    });
  }
  // 5
  setDefaultAddress(addrId) {
    Swal.fire({
      title: "Đặt địa chỉ này thành mặc định?",
      cancelButtonText: "Hủy",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Đặt"
    }).then(result => {
      if (result.isConfirmed) {
        this.addressService.setDefaultAddress(addrId).subscribe({
          next: addrRes => {
            this.notifService.success("Đặt địa chỉ mặc định thành công!");
            this.addresses = this.addresses.map(a => {
              if (a.id === addrRes.id) {
                a.macDinh = true;
              } else {
                a.macDinh = false;
              }
              return a;
            });
          },
          error: errorRes => {
            this.notifService.error(errorRes.error.message);
          }
        });
      }
    });
  }
  // select address event functions
  // get districts by province
  // 6
  getAllDistrictsByProvince(formType) {
    this.wards = [];
    this.getProvinceId(formType);
    this.giaoHangNhanhService.getAllDistrictByProvinceID(this.provinceId).subscribe({
      next: resp => {
        this.districts = resp.data;
        formType === "add" ? this.addAddressForm.get("tinh").setValue(this.getProvinceName(formType)) : this.updateAddressForm.get("tinh").setValue(this.getProvinceName(formType));
      }
    });
  }
  // 6.1
  getProvinceId(formType) {
    for (let i = 0; i < this.provinces.length; i++) {
      const element = this.provinces[i];
      if (formType === "add" && element.NameExtension.includes(this.addAddressForm.get("tinh").value)) {
        this.provinceId = element.ProvinceID;
        break;
      } else if (formType === "update" && element.NameExtension.includes(this.updateAddressForm.get("tinh").value)) {
        this.provinceId = element.ProvinceID;
        break;
      }
    }
  }
  // 6.2
  getProvinceName(formType) {
    let provinceName = formType === "add" ? this.addAddressForm.get("tinh").value : this.updateAddressForm.get("tinh").value;
    if (provinceName == null || provinceName == "") {
      this.provinces.forEach(p => {
        if (p.ProvinceID == this.provinceId) {
          provinceName = p.ProvinceName;
        }
      });
    }
    return provinceName;
  }
  // 7
  getAllWardsByDistrict(formType) {
    this.getDistrictId(formType);
    this.giaoHangNhanhService.getAllWardByDistrictID(this.districtId).subscribe({
      next: resp => {
        this.wards = resp.data;
        formType === "add" ? this.addAddressForm.get("huyen").setValue(this.getDistrictName(formType)) : this.updateAddressForm.get("huyen").setValue(this.getDistrictName(formType));
      }
    });
  }
  // 7.1
  getDistrictId(formType) {
    for (let i = 0; i < this.districts.length; i++) {
      const element = this.districts[i];
      if (formType === "add" && element.NameExtension.includes(this.addAddressForm.get("huyen").value)) {
        this.districtId = element.DistrictID;
        break;
      } else if (formType === "update" && element.NameExtension.includes(this.updateAddressForm.get("huyen").value)) {
        this.districtId = element.DistrictID;
        break;
      }
    }
  }
  // 7.2
  getDistrictName(formType) {
    let districtName = formType === "add" ? this.addAddressForm.get("huyen").value : this.updateAddressForm.get("huyen").value;
    this.districts.forEach(d => {
      if (d.DistrictID == this.districtId) {
        districtName = d.DistrictName;
      }
    });
    return districtName;
  }
  // 8
  checkProvinceSelection() {
    if (!this.provinceId) {
      this.notifService.warning("Vui lòng chọn tỉnh/thành phố trước!");
      return;
    }
  }
  // 9
  checkDistrictSelection() {
    if (!this.provinceId) {
      this.notifService.warning("Vui lòng chọn quận/huyện trước!");
      return;
    }
  }
  // end: select address event functions
  // 10
  openUpdateModal(addrId) {
    this.toggleUpdateAddressesModal(true);
    this.addressService.getById(addrId).subscribe({
      next: addrRes => {
        this.updateAddressForm = new FormGroup({
          id: new FormControl(addrId, []),
          hoTen: new FormControl(addrRes.hoTen, [Validators.required, Validators.pattern("^[a-zA-ZÀ-ỹ\\s]+$"), this.customRequiredValidator]),
          sdt: new FormControl(addrRes.sdt, [Validators.required, Validators.pattern("^(0[1-9][0-9]{8})$"), this.customRequiredValidator]),
          tinh: new FormControl(addrRes.tinh, [Validators.required]),
          huyen: new FormControl(addrRes.huyen, [Validators.required]),
          xa: new FormControl(addrRes.xa, [Validators.required]),
          duong: new FormControl(addrRes.duong, [Validators.required, Validators.pattern("^[a-zA-ZÀ-ỹ0-9-_/.\\s]+$"), this.customRequiredValidator])
        });
        this.getAllDistrictsByProvince("update");
        this.getAllWardsByDistrict("update");
      },
      error: err => {}
    });
  }
  // 11
  deleteAddress(addrId) {
    Swal.fire({
      title: "Xóa địa chỉ này?",
      cancelButtonText: "Hủy",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Xóa"
    }).then(result => {
      if (result.isConfirmed) {
        this.addressService.deleteAddress(addrId).subscribe({
          next: () => {
            this.notifService.success("Địa chỉ đã được xóa!");
