import { CurrencyPipe } from "@angular/common";
import { HttpErrorResponse } from "@angular/common/http";
import { Component, Input } from "@angular/core";
import { OwlOptions } from "ngx-owl-carousel-o";
import { MauSac } from "src/app/model/class/mau-sac.class";

import { SanPham } from "src/app/model/class/san-pham.class";
import { PagedResponse } from "src/app/model/interface/paged-response.interface";
import { ProductService } from "src/app/service/product.service";

@Component({
  selector: "app-dot-giam-gia",
  templateUrl: "./dot-giam-gia.component.html",
  styleUrls: ["./dot-giam-gia.component.css"],
})
export class DotGiamGiaComponent {
  dotGiamGiaProduct: OwlOptions = {
    loop: true,
    mouseDrag: true,
    touchDrag: true,
    pullDrag: true,
    dots: false,
    navSpeed: 800,
    navText: [
      '<i class="bi bi-caret-left"></i>',
      '<i class="bi bi-caret-right"></i>',
    ],
    responsive: {
      0: {
        items: 1,
      },
      400: {
        items: 2,
      },
      740: {
        items: 3,
      },
      940: {
        items: 4,
      },
    },
    nav: true,
  };

  @Input() saleProducts: SanPham[] = [];
  public hours: number = 0;
  public minutes: number = 0;
  public seconds: number = 0;
  intervalId: any;
  public hoursDisplay: any;
  public minutesDisplay: any;
  public secondsDisplay: any;

  // constructor, ngOn
  constructor(private currencyPipe: CurrencyPipe) {}

  ngOnInit(): void {
    this.getSanPhamList();

    // Thời Gian Sale
    // Set the date we're counting down to
    const countDownDate = new Date("Jan 16, 2025 15:37:25").getTime();

    // Update the count down every 1 second
    this.intervalId = setInterval(() => {
      // Get today's date and time
      const now = new Date().getTime();

      // Find the distance between now and the count down date
      const distance = countDownDate - now;

      // Time calculations for hours, minutes and seconds
      this.hours = Math.floor(
        (distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
      );
      this.minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
      this.seconds = Math.floor((distance % (1000 * 60)) / 1000);

      // Add leading zero if needed
      this.hoursDisplay = this.hours < 10 ? "0" + this.hours : this.hours;
      this.minutesDisplay =
        this.minutes < 10 ? "0" + this.minutes : this.minutes;
      this.secondsDisplay =
        this.seconds < 10 ? "0" + this.seconds : this.seconds;

      // If the count down is finished, write some text
      if (distance < 0) {
        clearInterval(this.intervalId);
        this.hoursDisplay = "00";
        this.minutesDisplay = "00";
        this.secondsDisplay = "00";
      }
    }, 1000);
  }

  // public functions
  // 1
  public displayPrice(sanPham: SanPham): any {
    const priceArr = [];
    for (let spct of sanPham.sanPhamChiTiets) {
      priceArr.push(spct.giaBan);
    }
    const minPrice = Math.min(...priceArr);
    const maxPrice = Math.max(...priceArr);
    if (minPrice === maxPrice) {
      return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0");
    }
    return (
      this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0") +
      " - " +
      this.currencyPipe.transform(maxPrice, "VND", "symbol", "1.0-0")
    );
  }

  // 2
  public getMauSacList(sanPham: SanPham): MauSac[] {
    const mauSacs: MauSac[] = [];
    for (let spct of sanPham.sanPhamChiTiets) {
      if (!this.checkExist(mauSacs, spct.mauSac.id)) {
        mauSacs.push(spct.mauSac);
      }
    }
    return mauSacs;
  }

  public displayDiscountPrice(prod: SanPham): any {
    const priceArr = [];
    for (let spct of prod.sanPhamChiTiets) {
      let saleEvent = prod.saleEvent;
      priceArr.push(
        Math.round((spct.giaBan * (100 - saleEvent.giaTriPhanTram)) / 100)
      );
    }
    const minPrice = Math.min(...priceArr);
    const maxPrice = Math.max(...priceArr);
    if (minPrice === maxPrice) {
      return this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0");
    }
    return (
      this.currencyPipe.transform(minPrice, "VND", "symbol", "1.0-0") +
      " - " +
      this.currencyPipe.transform(maxPrice, "VND", "symbol", "1.0-0")
    );
  }

  // private functions
  // 1
  private getSanPhamList(): void {
    // this.productService.getByPageClient().subscribe({
    //   next: (response: PagedResponse<SanPham>) => {
    //     this.pagedResponse = response;
    //   },
    //   error: (error: HttpErrorResponse) => {
    //     console.log(error);
    //   },
    // });
  }

  // 2
  private checkExist(mauSacs: MauSac[], mauSacId: number): boolean {
    for (let m of mauSacs) {
      if (m.id === mauSacId) {
        return true;
      }
    }
    return false;
  }
}
<div class="slide-container swiper">
  <div class="demo1">
    <span
      ><img
        width="20"
        height="20"
        src="https://file.hstatic.net/1000253775/file/flash_c1195bb7597344909c3a855990d45510.png"
        alt="FLASH SALE"
      />FLASH SALE
    </span>

    <!-- <div class="sale-time">
      <div class="countdown-item hour">
        <p>{{ hoursDisplay }}</p>
        <span>Giờ</span>
      </div>
      <div class="countdown-item minute">
        <p>{{ minutesDisplay }}</p>
        <span>Phút</span>
      </div>
      <div class="countdown-item second">
        <p>{{ secondsDisplay }}</p>
        <span>Giây</span>
      </div>
    </div> -->
  </div>

  <section class="policy-product">
    <div class="container">
      <owl-carousel-o [options]="dotGiamGiaProduct">
        <ng-template
          carouselSlide
          *ngFor="let sp of saleProducts"
          style="background-color: black"
        >
          <div class="product">
            <div class="product-thumbnail">
              <a class="image_thumb">
                <img [src]="sp?.sanPhamChiTiets[0]?.hinhAnhs[0]?.imageUrl" />
              </a>
              <div class="saleProduct">{{ sp.saleEvent.giaTriPhanTram }} %</div>
            </div>

            <!-- <div class="details">
              <h3 class="nameProduct">
                <a [routerLink]="'/san-pham/' + sp.id">{{ sp.ten }}</a>
              </h3>
              <p class="priceProduct">{{ displayPrice(sp) }}</p>
              <div class="swatch-color-wrapper mb-3">
                <div
                  data-value="Đen"
                  class="swatch-element active"
                  *ngFor="let color of getMauSacList(sp)"
                >
                  <img class="img-color" [src]="color.image.imageUrl" />
                </div>
              </div>
            </div> -->

            <div class="details">
              <h3 class="nameProduct">
                <a [routerLink]="'/san-pham/' + sp.id">{{ sp.ten }}</a>
              </h3>

              <p class="priceProduct" *ngIf="sp.saleEvent">
                <s>{{ displayPrice(sp) }}</s>
              </p>
              <p class="priceProduct text-danger" *ngIf="sp.saleEvent">
                {{ displayDiscountPrice(sp) }}
              </p>
              <p class="priceProduct" *ngIf="!sp.saleEvent">
                {{ displayPrice(sp) }}
              </p>

              <div class="swatch-color-wrapper">
                <div
                  class="swatch-element"
                  *ngFor="let color of getMauSacList(sp)"
                >
                  <img class="img-color" [src]="color.image.imageUrl" />
                </div>
              </div>
            </div>
          </div>
        </ng-template>
      </owl-carousel-o>
    </div>
  </section>
</div>
