import { CurrencyPipe } from "@angular/common";
import { Component, Input } from "@angular/core";

import { Discount } from "src/app/model/class/discount.class";

@Component({
  selector: "app-phieu-giam-gia",
  templateUrl: "./phieu-giam-gia.component.html",
  styleUrls: ["./phieu-giam-gia.component.css"],
})
export class PhieuGiamGiaComponent {
  @Input() discounts: Discount[] = [];

  // constructor, ngOn
  constructor(private currencyPipe: CurrencyPipe) {}

  // public functions
  // 1
  public formatPrice(price: number): any {
    return this.currencyPipe.transform(price, "VND", "symbol", "1.0-0");
  }

  // 2
  public getDiscountTitle(discount: Discount): string {
    if (discount?.kieu === 0) {
      return ` Giảm ${discount?.giaTri}% cho đơn  từ ${this.formatPrice(
        discount?.dieuKienGiam
      )} Tối đa ${this.formatPrice(discount.giaTriMax)}`;
    } else {
      return ` Giảm ${this.formatPrice(
        discount?.giaTri
      )} cho đơn  từ ${this.formatPrice(discount?.dieuKienGiam)}`;
    }
  }

  // 3
  public getBriefDiscountTitle(discount: Discount): string {
    if (discount?.kieu === 0) {
      return `Giảm ${discount?.giaTri}%`;
    } else {
      return ` Giảm ${this.formatPrice(discount?.giaTri)}`;
    }
  }
}
<div class="container">
  <div class="overflow-x-auto-mb">
    <div class="wd-list-coupon">
      <div class="item" *ngFor="let discount of discounts">
        <div class="wd-coupon d-flex">
          <div class="wd-coupon-left d-flex">
            <strong> {{ discount.tenPhieuGiamGia }} </strong>
          </div>
          <div class="wd-coupon-right d-flex">
            <div class="wd-coupon-right-top">
              <div class="coupon_title">
                <span class="big-bold">{{
                  getBriefDiscountTitle(discount)
                }}</span>
              </div>
              <p class="tilte2020">{{ getDiscountTitle(discount) }}</p>
            </div>

            <div class="wd-coupon-right-bottom d-flex d-flex-center">
              <div class="wd-coupon-detail d-flex">
                <span
                  >Mã:
                  <strong>{{ discount.maPhieuGiamGia }}</strong>
                </span>
                <span
                  >HSD:
                  {{
                    discount.thoiGianKetThuc | date : "dd-MM-yyyy HH:mm"
                  }}</span
                >
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
