import { HttpErrorResponse } from "@angular/common/http";
import { Component } from "@angular/core";
import { FormControl, FormGroup, Validators } from "@angular/forms";

import { Customer } from "src/app/model/class/customer.class";
import { CustomerResponse } from "src/app/model/interface/customer-response.interface";
import { UpdateCustInfoReq } from "src/app/model/interface/update-cust-info-req.interface";
import { AuthenticationService } from "src/app/service/authentication.service";
import { CustomerService } from "src/app/service/customer.service";
import { NotificationService } from "src/app/service/notification.service";
import Swal, { SweetAlertResult } from "sweetalert2";

@Component({
  selector: "app-my-account",
  templateUrl: "./my-account.component.html",
  styleUrls: ["./my-account.component.css"],
})
export class MyAccountComponent {
  public form: FormGroup;
  private loggedCust: Customer;

  // constructor, ngOn
  constructor(
    private authService: AuthenticationService,
    private customerService: CustomerService,
    private notifService: NotificationService
  ) {}

  ngOnInit(): void {
    this.initForm();
  }

  // public functions
  //
  public updateInfor(): void {
    Swal.fire({
      title: "Thay đổi thông tin tài khoản?",
      cancelButtonText: "Hủy",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Lưu",
    }).then((result: SweetAlertResult) => {
      if (result.isConfirmed) {
        const req: UpdateCustInfoReq = {
          custId: this.loggedCust.id,
          fullName: this.form.get("hoTen").value.trim(),
          phone: this.form.get("sdt").value.trim(),
          birthday: this.form.get("ngaySinh").value,
          gender: this.form.get("gioiTinh").value,
        };

        this.customerService.updateInfo(req).subscribe({
          next: (custRes: Customer) => {
            this.notifService.success(
              "Cập nhật thông tin tài khoản thành công!"
            );
            this.authService.saveCustomerToStorage(custRes);
            this.initForm();
          },
          error: (errorRes: HttpErrorResponse) => {
            this.notifService.error(errorRes.error.message);
          },
        });
      }
    });
  }

  // private functions
  // 1
  private initForm(): void {
    this.form = new FormGroup({
      email: new FormControl("", [Validators.required]),
      gioiTinh: new FormControl("", [Validators.required]),
      hoTen: new FormControl("", [Validators.required]),
      ngaySinh: new FormControl("", [Validators.required]),
      sdt: new FormControl("", [Validators.required]),
      tenDangNhap: new FormControl("", [Validators.required]),
    });

    this.loggedCust = this.authService.getCustomerFromStorage();

    this.customerService.getById(this.loggedCust.id).subscribe({
      next: (custRes: CustomerResponse) => {
        this.form = new FormGroup({
          email: new FormControl(custRes.email),
          gioiTinh: new FormControl(custRes.gioiTinh, [Validators.required]),
          hoTen: new FormControl(custRes.hoTen, [
            Validators.required,
            Validators.pattern("^[a-zA-ZÀ-ỹ\\s]+$"),
            this.customRequiredValidator,
          ]),
          ngaySinh: new FormControl(custRes.ngaySinh, [
            Validators.required,
            this.pastDateValidator,
          ]),
          sdt: new FormControl(custRes.sdt, [
            Validators.required,
            Validators.pattern("^(0[1-9][0-9]{8})$"),
            this.customRequiredValidator,
          ]),
          tenDangNhap: new FormControl(custRes.tenDangNhap),
        });
      },
      error: (errResp: HttpErrorResponse) => {
        this.notifService.error(errResp.error.message);
      },
    });
  }

  // 1.1
  private pastDateValidator(
    control: FormControl
  ): { [key: string]: boolean } | null {
    const selectedDate = new Date(control.value);
    const currentDate = new Date();

    if (selectedDate >= currentDate) {
      return { invalidDate: true };
    }
    return null;
  }

  // 1.2
  private customRequiredValidator(
    control: FormControl
  ): { [key: string]: boolean } | null {
    const value = control.value;

    if (value.trim() === "") {
      return { customRequired: true };
    }
    return null;
  }
}
<div class="container mb-5 mt-5">
  <div class="row">
    <div class="wrapperChucNang col-md-3">
      <app-profile-sidebar></app-profile-sidebar>
    </div>
    <div class="wrapperTaiKhoan col-md-8 p-3">
      <div>
        <div>
          <div class="account-setting-form">
            <div class="account-page-title">
              <h1 class="title">Thông tin tài khoản</h1>
            </div>
            <form [formGroup]="form">
              <div class="form-group form-gender">
                <div class="label">Giới tính</div>
                <div class="control">
                  <label for="radio1"
                    ><input
                      type="radio"
                      name="gioiTinh"
                      id="radio1"
                      [value]="true"
                      formControlName="gioiTinh"
                    />
                    <span>Nam</span></label
                  >
                  <label for="radio2"
                    ><input
                      type="radio"
                      name="gioiTinh"
                      id="radio2"
                      [value]="false"
                      formControlName="gioiTinh"
                    />
                    <span>Nữ</span></label
                  >
                </div>
              </div>

              <!-- Họ tên -->
              <div class="form-group active">
                <label>Họ tên:</label>
                <input
                  type="text"
                  class="form-control"
                  formControlName="hoTen"
                  [ngClass]="
                    form.get('hoTen').invalid && form.get('hoTen').touched
                      ? 'is-invalid'
                      : ''
                  "
                />
                <div
                  class="invalid-feedback"
                  *ngIf="form.get('hoTen').hasError('customRequired')"
                >
                  Họ tên không được bỏ trống
                </div>
                <div
                  class="invalid-feedback"
                  *ngIf="form.get('hoTen').hasError('pattern')"
                >
                  Họ tên không đúng định dạng
                </div>
              </div>

              <!-- SĐT -->
              <div class="form-group active">
                <label>Số điện thoại:</label>
                <input
                  type="text"
                  class="form-control"
                  formControlName="sdt"
                  [ngClass]="
                    form.get('sdt').invalid && form.get('sdt').touched
                      ? 'is-invalid'
                      : ''
                  "
                />
                <div
                  class="invalid-feedback"
                  *ngIf="form.get('sdt').hasError('customRequired')"
                >
                  SĐT không được bỏ trống
                </div>
                <div
                  class="invalid-feedback"
                  *ngIf="
                    form.get('sdt').hasError('pattern') &&
                    !form.get('sdt').hasError('customRequired')
                  "
                >
                  SĐT không đúng định dạng
                </div>
              </div>

              <div class="form-group active">
                <label>Email (readonly):</label>
                <input
                  readonly
                  type="text"
                  class="form-control"
                  formControlName="email"
                />
              </div>

              <!-- Ngày sinh -->
              <div class="form-group active">
                <label>Ngày sinh:</label>
                <input
                  type="date"
                  class="form-control"
                  formControlName="ngaySinh"
                  [ngClass]="
                    form.get('ngaySinh').invalid && form.get('ngaySinh').touched
                      ? 'is-invalid'
                      : ''
                  "
                />
                <div
                  class="invalid-feedback"
                  *ngIf="form.get('ngaySinh').hasError('required')"
                >
                  Ngày sinh không được bỏ trống
                </div>
                <div
                  class="invalid-feedback"
                  *ngIf="form.get('ngaySinh').hasError('invalidDate')"
                >
                  Ngày sinh phải sau ngày hiện tại
                </div>
              </div>

              <div class="form-group active">
                <label>Tên đăng nhập (readonly):</label>
                <input
                  readonly
                  type="text"
                  class="form-control"
                  formControlName="tenDangNhap"
                />
              </div>

              <div class="form-button text-center">
                <button
                  class="btn btn-primary"
                  (click)="updateInfor()"
                  style="width: 200px"
                  [disabled]="form.invalid"
                >
                  Lưu thay đổi
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
