oke: #da291c;\n}\n\n.order-tracking[_ngcontent-%COMP%] {\n  position: relative;\n  margin-left: auto;\n}\n\n.order-tracking[_ngcontent-%COMP%]   .order-tracking-status-list[_ngcontent-%COMP%] {\n  background: #fff;\n  box-shadow: 0 2px 10px rgba(51, 63, 72, 0.1);\n  border-radius: 2px;\n  padding: 20px 16px;\n  z-index: 30;\n  right: 0;\n  top: 100%;\n  position: absolute;\n  width: 375px;\n  margin-top: 10px;\n  display: none;\n}\n\n.order-tracking.active[_ngcontent-%COMP%]   .order-tracking-status-list[_ngcontent-%COMP%] {\n  display: block;\n}\n\nimg[_ngcontent-%COMP%] {\n  max-width: 100%;\n  height: auto;\n  width: auto;\n}\n\n\n\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%] {\n  display: block;\n  width: 100%;\n  margin-bottom: 35px;\n  text-align: center;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon-wrap[_ngcontent-%COMP%] {\n  display: block;\n  position: relative;\n  width: 100%;\n  height: 80px;\n  text-align: center;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon-wrap[_ngcontent-%COMP%]::before, .steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon-wrap[_ngcontent-%COMP%]::after {\n  display: block;\n  position: absolute;\n  top: 50%;\n  width: 50%;\n  height: 3px;\n  margin-top: -1px;\n  background-color: #e1e7ec;\n  content: \"\";\n  z-index: 1;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon-wrap[_ngcontent-%COMP%]::before {\n  left: 0;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon-wrap[_ngcontent-%COMP%]::after {\n  right: 0;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-icon[_ngcontent-%COMP%] {\n  display: inline-block;\n  position: relative;\n  width: 80px;\n  height: 80px;\n  border: 1px solid #e1e7ec;\n  border-radius: 50%;\n  background-color: #f5f5f5;\n  color: #374250;\n  font-size: 38px;\n  line-height: 81px;\n  z-index: 5;\n}\n\n.steps[_ngcontent-%COMP%]   .step[_ngcontent-%COMP%]   .step-title[_ngc