import { CurrencyPipe } from "@angular/common";
import { HttpErrorResponse } from "@angular/common/http";
import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";

import Swal, { SweetAlertResult } from "sweetalert2";

import { OrderHistory } from "src/app/model/class/order-history.class";
import { Order } from "src/app/model/class/order.class";
import { ChangeOrderStatusReq } from "src/app/model/interface/change-order-status-req.interface";
import { NotificationService } from "src/app/service/notification.service";
import { OrderService } from "src/app/service/order.service";
import { Payment } from "src/app/model/class/payment.class";
import { AddNotificationReq } from "src/app/model/interface/add-notifi-req.interface";
import { Customer } from "src/app/model/class/customer.class";
import { AuthenticationService } from "src/app/service/authentication.service";
import { NotifService } from "src/app/service/notif.service";
import { WebSocketService } from "src/app/service/web-socket.service";

@Component({
  selector: "app-order-tracking",
  templateUrl: "./order-tracking.component.html",
  styleUrls: ["./order-tracking.component.css"],
})
export class OrderTrackingComponent {
  public loggedCust: Customer;
  public order: Order;
  public totalSalePrice: number = 0;
  private webSocket!: WebSocket;
  public orderStatusList = [
    "CHO_XAC_NHAN",
    "DA_XAC_NHAN",
    "CHO_GIAO",
    "DANG_GIAO",
    "HOAN_THANH",
    "HUY",
    "TRA_HANG",
    "CHO_HOAN_TIEN",
    "DA_HOAN_TIEN",
  ];
  public isLoadding = false;
  public overlayText: string = "";

  // constructor, ngOn
  constructor(
    private activatedRoute: ActivatedRoute,
    private currencyPipe: CurrencyPipe,
    private notifService: NotificationService,
    private orderService: OrderService,
    private authService: AuthenticationService,
    private notifService2: NotifService,
    private webSocketService: WebSocketService
  ) {}

  ngOnInit(): void {
    this.webSocketService.openWebSocket();
    this.openWebSocket();
    this.loggedCust = this.authService.getCustomerFromStorage();
    this.getOrderByCode();
  }

  // public functions
  // 1
  public formatPrice(price: number): any {
    return this.currencyPipe.transform(price, "VND", "symbol", "1.0-0");
  }

  // 2
  public getOrderStatusIcon(orderHistory: OrderHistory): string {
    if (orderHistory.trangThai === "CHO_XAC_NHAN") {
      return "fa-solid fa-exclamation";
    } else if (orderHistory.trangThai === "DA_XAC_NHAN") {
      return "fa-solid fa-check";
    } else if (orderHistory.trangThai === "CHO_GIAO") {
      return "fa-solid fa-clock";
    } else if (orderHistory.trangThai === "DANG_GIAO") {
      return "fa-solid fa-truck-fast";
    } else if (orderHistory.trangThai === "HOAN_THANH") {
      return "fa-solid fa-credit-card";
    } else if (orderHistory.trangThai === "HUY") {
      return "fa-solid fa-ban";
    } else if (orderHistory.trangThai === "TRA_HANG") {
      return "fa-solid fa-rotate-left";
    } else if (orderHistory.trangThai === "CHO_HOAN_TIEN") {
      return "fa-solid fa-comment-dollar";
    } else if (orderHistory.trangThai === "DA_HOAN_TIEN") {
      return "fa-solid fa-money-bill-transfer";
    }
    return "";
  }

  // 3
  public getOrderStatus(orderStatus: string): string {
    if (orderStatus === "CHO_XAC_NHAN") {
      return "Chờ xác nhận";
    } else if (orderStatus === "DA_XAC_NHAN") {
      return "Đã xác nhận";
    } else if (orderStatus === "CHO_GIAO") {
      return "Chờ giao";
    } else if (orderStatus === "DANG_GIAO") {
      return "Đang giao";
    } else if (orderStatus === "HOAN_THANH") {
      return "Hoàn thành";
    } else if (orderStatus === "HUY") {
      return "Hủy";
    } else if (orderStatus === "TRA_HANG") {
      return "Trả hàng";
    } else if (orderStatus === "CHO_HOAN_TIEN") {
      return "Chờ hoàn tiền";
    } else if (orderStatus === "DA_HOAN_TIEN") {
      return "Đã hoàn tiền";
    }
    return "";
  }

  // 4
  public filterOrderHistory(orderHistory: OrderHistory[]): OrderHistory[] {
    return orderHistory?.filter((oh: OrderHistory) =>
      this.orderStatusList.includes(oh.trangThai)
    );
  }

  // 5
  public cancelOrder(): void {
    Swal.fire({
      title: "Hủy đơn hàng?",
      cancelButtonText: "Cancel",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "OK",
    }).then((result: SweetAlertResult) => {
      if (result.isConfirmed) {
        this.turnOnOverlay("Vui lòng chờ!");
        const req: ChangeOrderStatusReq = {
          idHoaDon: this.order.id,
          moTa: "123p1jppwkdpoakdsd",
          isNext: false,
        };
        this.orderService.cancelOrder(req).subscribe({
          next: () => {
            this.notifService.success("Hủy đơn hàng thành công!");
            this.getOrderByCode();
            this.sendMessage();
            this.turnOffOverlay("");
          },
          error: (errResp: HttpErrorResponse) => {
            this.notifService.error(errResp.error.message);
            this.turnOffOverlay("");
          },
        });
      }
    });
  }

  private sendMessage(): void {
    const newNotif: AddNotificationReq = {
      type: "CANCEL_ORDER",
      content: `Khách hàng ${this.loggedCust.hoTen} đã hủy đơn hàng!`,
      relatedUrl: `/hoa-don/chi-tiet-hoa-don/${this.order.id}`,
      custId: null,
    };
    this.notifService2.createNewNotification(newNotif).subscribe({
      next: (data) => {
        this.webSocketService.sendMessage(
          `Khách hàng ${this.loggedCust.hoTen} đã hủy đơn hàng!`
        );
      },
      error: (errResp: HttpErrorResponse) => {
        this.notifService.error(errResp.error.message);
      },
    });
  }

  public checkPaymentMethod(order: Order): string {
    if (
      order?.thanhToans.some(
        (p: Payment) => p.hinhThucThanhToan.hinhThuc === "CHUYEN_KHOAN"
      )
    ) {
      return "VNPAY";
    } else if (
      order?.thanhToans.some(
        (p: Payment) => p.hinhThucThanhToan.hinhThuc === "TIEN_MAT"
      ) ||
      order?.thanhToans.length === 0
    ) {
      return "TIEN_MAT";
    }
    return "";
  }

  // private functions
  // 1
  private getOrderByCode(): void {
    this.activatedRoute.params.subscribe((params) => {
      let orderCode = params["orderCode"];

      // 1.1 get order
      this.orderService.getByCode(orderCode).subscribe({
        next: (order: Order) => {
          this.order = order;
          //
          for (let od of this.order.hoaDonChiTiets) {
            if (od.sanPhamChiTiet.giaBan !== od.giaBan) {
              this.totalSalePrice +=
                (od.sanPhamChiTiet.giaBan - od.giaBan) * od.soLuong;
            }
          }
        },
        error: (errResp: HttpErrorResponse) => {
          this.notifService.error(errResp.error.message);
        },
      });
    });
  }

  // 2
  private openWebSocket(): void {
    this.webSocket = new WebSocket("ws://localhost:8080/notification");
    this.webSocket.onopen = (event) => {};
    this.webSocket.onmessage = (event) => {
      this.getOrderByCode();
      this.notifService.success(event.data as string);
    };
    this.webSocket.onclose = (event) => {};
  }

  // 3
  private turnOnOverlay(text: string): void {
    this.overlayText = text;
    this.isLoadding = true;
  }

  // 4
  private turnOffOverlay(text: string): void {
    this.overlayText = text;
    this.isLoadding = false;
  }
}
<div class="container mb-5 mt-5">
  <div class="row">
    <div class="wrapperChucNang col-md-3">
      <app-profile-sidebar></app-profile-sidebar>
    </div>

    <div class="wrapperTaiKhoan col-md-8 p-3">
      <div class="order-back-page">
        <a routerLink="/profile/my-orders"
          ><button class="btn">Quay lại</button></a
        >
      </div>
      <div class="account-page-title">
        <h1 class="title">Chi tiết đơn hàng</h1>
      </div>
      <div>
        <!-- timeline -->
        <div class="card mb-5">
          <div class="card-body">
            <div
              class="steps d-flex flex-wrap flex-sm-nowrap justify-content-between padding-top-2x padding-bottom-1x"
            >
              <div
                class="step completed"
                *ngFor="let oh of filterOrderHistory(order?.lichSuHoaDons)"
              >
                <div class="step-icon-wrap">
                  <div class="step-icon">
                    <i [ngClass]="getOrderStatusIcon(oh)"></i>
                  </div>
                </div>
                <h4 class="step-title">{{ oh.tieuDe }}</h4>
                <span>{{ oh?.createdAt }}</span>
              </div>
            </div>
          </div>
        </div>

        <ul class="order-details-view">
          <li>
            <label>Mã đơn</label>
            <div class="value">
              <b>{{ order?.ma }}</b>
              <div class="action-copy">
                <svg
                  width="16"
                  height="16"
                  viewBox="0 0 16 16"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <g clip-path="url(#clip0_3850_11339)">
                    <path
                      d="M10.6668 5.33301V3.46634C10.6668 2.7196 10.6668 2.34624 10.5215 2.06102C10.3937 1.81014 10.1897 1.60616 9.93882 1.47833C9.6536 1.33301 9.28023 1.33301 8.5335 1.33301H3.46683C2.72009 1.33301 2.34672 1.33301 2.06151 1.47833C1.81063 1.60616 1.60665 1.81014 1.47882 2.06102C1.3335 2.34624 1.3335 2.7196 1.3335 3.46634V8.53301C1.3335 9.27974 1.3335 9.65311 1.47882 9.93833C1.60665 10.1892 1.81063 10.3932 2.06151 10.521C2.34672 10.6663 2.72009 10.6663 3.46683 10.6663H5.3335M7.46683 14.6663H12.5335C13.2802 14.6663 13.6536 14.6663 13.9388 14.521C14.1897 14.3932 14.3937 14.1892 14.5215 13.9383C14.6668 13.6531 14.6668 13.2797 14.6668 12.533V7.46634C14.6668 6.7196 14.6668 6.34624 14.5215 6.06102C14.3937 5.81014 14.1897 5.60616 13.9388 5.47833C13.6536 5.33301 13.2802 5.33301 12.5335 5.33301H7.46683C6.72009 5.33301 6.34672 5.33301 6.06151 5.47833C5.81063 5.60616 5.60665 5.81014 5.47882 6.06102C5.3335 6.34624 5.3335 6.7196 5.3335 7.46634V12.533C5.3335 13.2797 5.3335 13.6531 5.47882 13.9383C5.60665 14.1892 5.81063 14.3932 6.06151 14.521C6.34672 14.6663 6.72009 14.6663 7.46683 14.6663Z"
                      stroke="#63B1BC"
                      stroke-width="1.2"
                      stroke-linecap="round"
                      stroke-linejoin="round"
                    ></path>
                  </g>
                  <defs>
                    <clipPath id="clip0_3850_11339">
                      <rect width="16" height="16" fill="white"></rect>
                    </clipPath>
                  </defs>
                </svg>
              </div>
            </div>
          </li>

          <li>
            <label>Ngày mua hàng:</label>
            <div class="value">
              {{ order?.createdAt }}
            </div>
          </li>
          <li>
            <label>Người nhận:</label>
            <div class="value">{{ order?.tenNguoiNhan }}</div>
          </li>
          <li>
            <label>Số điện thoại:</label>
            <div class="value">{{ order?.sdtNguoiNhan }}</div>
          </li>
          <li>
            <label>Địa chỉ nhận:</label>
            <div class="value">{{ order?.diaChiNguoiNhan }}</div>
          </li>
          <li>
            <label>Hình thức thanh toán:</label>
            <div class="value" *ngIf="checkPaymentMethod(order) === 'TIEN_MAT'">
              Thanh toán khi nhận hàng (COD)
            </div>
            <div class="value" *ngIf="checkPaymentMethod(order) === 'VNPAY'">
              VN Pay
            </div>
          </li>
          <li>
            <label>Trạng thái: </label>
            <div class="value">
              <span class="status cancel">{{
                getOrderStatus(order?.trangThai)
              }}</span>
            </div>
          </li>
        </ul>

        <!-- order details -->
        <div class="order-details-items">
          <h2 class="title">
            Sản phẩm <span>({{ order?.hoaDonChiTiets.length }})</span>
          </h2>
          <table class="table-order-items">
            <tbody>
              <tr *ngFor="let od of order?.hoaDonChiTiets">
                <td class="col name">
                  <div class="order-product">
                    <div class="order-product-photo">
                      <img
                        class="rounded"
                        [src]="od.sanPhamChiTiet.hinhAnhs[0].imageUrl"
                      />
                    </div>
                    <div class="order-product-details">
                      <div class="order-product-name">
                        {{
                          od.sanPhamChiTiet.sanPham.ten +
                            " - " +
                            od.sanPhamChiTiet.id
                        }}
                      </div>
                      <div class="order-product-options">
                        <div class="order-product-option">
                          <span
                            style="
                              display: flex;
                              flex-direction: row;
                              align-items: center;
                            "
                          >
                            <img
                              [src]="od.sanPhamChiTiet.mauSac?.image?.imageUrl"
                              width="20px"
                            />
                            <span>{{
                              od.sanPhamChiTiet.mauSac.ten
                            }}</span></span
                          >
                        </div>
                        <div class="order-product-option">
                          <span>{{
                            od.sanPhamChiTiet.kichCo.ten.split("-")[0]
                          }}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                </td>
                <td class="col qty">{{ od.soLuong }}</td>
                <td class="col price">
                  <span
                    class="price"
                    *ngIf="od.giaBan !== od.sanPhamChiTiet.giaBan"
                    >{{ formatPrice(od.giaBan) }}</span
                  >
                  <span
                    class="old-price"
                    *ngIf="od.giaBan !== od.sanPhamChiTiet.giaBan"
                    >{{ formatPrice(od.sanPhamChiTiet.giaBan) }}</span
                  >
                  <span
                    class="price"
                    *ngIf="od.giaBan === od.sanPhamChiTiet.giaBan"
                    >{{ formatPrice(od.giaBan) }}</span
                  >
                </td>
                <td class="col price">
                  <span
                    class="price"
                    *ngIf="od.giaBan !== od.sanPhamChiTiet.giaBan"
                    >{{ formatPrice(od.giaBan * od.soLuong) }}</span
                  >
                  <span
                    class="old-price"
                    *ngIf="od.giaBan !== od.sanPhamChiTiet.giaBan"
                    >{{
                      formatPrice(od.sanPhamChiTiet.giaBan * od.soLuong)
                    }}</span
                  >
                  <span
                    class="price"
                    *ngIf="od.giaBan === od.sanPhamChiTiet.giaBan"
                    >{{ formatPrice(od.giaBan * od.soLuong) }}</span
                  >
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        <!-- money -->
        <div class="order-details-totals">
          <table>
            <tbody>
              <tr class="sub-totals">
                <th>Giá trị đơn hàng:</th>
                <td>{{ formatPrice(order?.tongTien) }}</td>
              </tr>
              <tr class="sub-totals">
                <th>Giảm giá:</th>
                <td style="color: rgb(218, 41, 28)">
                  {{ formatPrice(order?.tienGiam) }}
                </td>
              </tr>
              <tr class="sub-totals">
                <th>Phí vận chuyển</th>
                <td>
                  {{ formatPrice(order?.phiVanChuyen) }}
                </td>
              </tr>
            </tbody>
            <tfoot>
              <tr class="grand-totals">
                <th style="color: rgb(51, 63, 72)">Tổng tiền thanh toán</th>
                <td>
                  {{
                    formatPrice(
                      order?.tongTien - order?.tienGiam + order?.phiVanChuyen
                    )
                  }}
                </td>
              </tr>
            </tfoot>
          </table>
        </div>

        <div class="mt-5">
          <button
            class="btn btn-outline-danger"
            [disabled]="
              !(
                order?.trangThai === 'CHO_XAC_NHAN' ||
                order?.trangThai === 'DA_XAC_NHAN' ||
                order?.trangThai === 'CHO_GIAO'
              )
            "
            (click)="cancelOrder()"
          >
            Hủy đơn hàng
          </button>
        </div>
      </div>
    </div>
  </div>
</div>
<app-overlay *ngIf="isLoadding" [text]="overlayText"></app-overlay>
