import { Component } from "@angular/core";
import { OwlOptions } from "ngx-owl-carousel-o";

@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.css"],
})
export class HeaderComponent {
  headerSlide: OwlOptions = {
    loop: true,
    mouseDrag: true,
    touchDrag: true,
    pullDrag: false,
    dots: false,
    autoplay: true,
    autoplayTimeout: 2000,
    autoplayHoverPause: true,
    navSpeed: 700,
    navText: ["", ""],
    responsive: {
      0: {
        items: 1,
      },
      400: {
        items: 1,
      },
      740: {
        items: 1,
      },
      940: {
        items: 1,
      },
    },
    nav: false,
  };
}
<header class="container">
  <owl-carousel-o [options]="headerSlide">
    <ng-template carouselSlide>
      <img
        src="https://i.imgur.com/EvEw8qN.jpeg"
        class="d-block w-100"
        alt="..."
    /></ng-template>
    <ng-template carouselSlide
      ><img
        src="https://i.imgur.com/fMrOI7b.jpeg"
        class="d-block w-100"
        alt="..."
    /></ng-template>
    <ng-template carouselSlide
      ><img
        src="https://i.imgur.com/7cCzZ9Q.jpeg"
        class="d-block w-100"
        alt="..."
    /></ng-template>
  </owl-carousel-o>
</header>
